{
    "name": "Kafka, HTTP, and Confluent Cloud",
    "apiVersion": "1.0",
    "definitionVersion": "4.0.0",
    "type": "message",
    "description": "Kafka, HTTP, and Confluent Cloud",
    "listeners": [
        {
            "type": "http",
            "paths": [
                {
                    "path": "/data/ingestion/kafka"
                }
            ],
            "entrypoints": [
                {
                    "type": "http-post",
                    "configuration": {
                        "requestHeadersToMessage": false
                    }
                }
            ]
        },
        {
            "type": "http",
            "paths": [
                {
                    "path": "/demo/http-get"
                }
            ],
            "entrypoints": [
                {
                    "type": "http-get",
                    "configuration": {
                        "messagesLimitCount": 1,
                        "messagesLimitDurationMs": 10000,
                        "headersInPayload": false,
                        "metadataInPayload": false
                    }
                }
            ]
        }
    ],
    "endpointGroups": [
        {
            "name": "default",
            "type": "kafka-advanced",
            "endpoints": [
                {
                    "name": "default",
                    "type": "kafka-advanced",
                    "weight": 1,
                    "inheritConfiguration": false,
                    "configuration": {
                        "bootstrapServers": "{{kafkabootstrap}}",
                        "topics" : ["demo"],
                         "security" : {
                            "protocol" : "SASL_SSL",
                            "sasl": {
                                "saslMechanism" : "PLAIN",
                                "saslJaasConfig" : "org.apache.kafka.common.security.plain.PlainLoginModule required username=\"{{kafkakey}}\" password=\"{{kafkasecret}}\";"
                            }
                        },
                        "consumer" : {
                            "enabled": true
                        },
                        "producer": {
                            "enabled": true
                        }
                    }
                }
            ]
        }
    ],
    "flows": [
        {
            "name": "",
            "selectors": [],
            "request": [],
            "response": [],
            "subscribe": [],
            "publish": [],
            "enabled": true
        }
    ]
}

